import { useState, useEffect } from "react";
import { Search, X } from "lucide-react";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { useLanguage } from "@/contexts/LanguageContext";

interface Gift {
  id: string;
  name: string;
  image: string;
}

interface GiftPickerModalProps {
  open: boolean;
  onClose: () => void;
  gifts: Gift[];
  selectedGiftIds?: string[];
  onSelectGifts: (giftIds: string[]) => void;
}

export default function GiftPickerModal({
  open,
  onClose,
  gifts,
  selectedGiftIds = [],
  onSelectGifts,
}: GiftPickerModalProps) {
  const { t } = useLanguage();
  const [searchQuery, setSearchQuery] = useState("");
  const [localSelectedIds, setLocalSelectedIds] = useState<string[]>(selectedGiftIds);

  useEffect(() => {
    if (open) {
      setLocalSelectedIds(selectedGiftIds);
      setSearchQuery("");
    }
  }, [open, selectedGiftIds]);

  const filteredGifts = gifts.filter((gift) =>
    gift.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const handleToggleGift = (giftId: string) => {
    if (giftId === "") {
      setLocalSelectedIds([]);
    } else {
      setLocalSelectedIds((prev) =>
        prev.includes(giftId)
          ? prev.filter((id) => id !== giftId)
          : [...prev, giftId]
      );
    }
  };

  const handleApply = () => {
    onSelectGifts(localSelectedIds);
    onClose();
  };

  const handleCancel = () => {
    setLocalSelectedIds([]);
    setSearchQuery("");
    onClose();
  };

  return (
    <Dialog open={open} onOpenChange={(open) => { if (!open) return; handleCancel(); }}>
      <DialogContent className="max-w-[95vw] sm:max-w-md max-h-[90vh] p-0 bg-card border-card-border rounded-2xl mx-4">
        <DialogHeader className="px-4 sm:px-6 pt-5 sm:pt-6 pb-3 sm:pb-4 border-b border-border">
          <div className="flex items-center justify-between">
            <div>
              <DialogTitle className="text-foreground text-lg sm:text-xl font-bold">
                üéÅ {t.giftPicker.title}
              </DialogTitle>
              {localSelectedIds.length > 0 && (
                <span className="text-primary text-xs sm:text-sm font-medium mt-1 block">
                  {t.giftPicker.selected(localSelectedIds.length)}
                </span>
              )}
            </div>
            <Button
              variant="ghost"
              size="icon"
              onClick={handleCancel}
              className="w-9 h-9 sm:w-10 sm:h-10 rounded-full hover:bg-muted flex-shrink-0"
            >
              <X className="w-4 h-4 sm:w-5 sm:h-5 text-muted-foreground" />
            </Button>
          </div>
          <DialogDescription className="text-muted-foreground text-xs sm:text-sm mt-1.5">
            {t.giftPicker.description}
          </DialogDescription>
        </DialogHeader>

        <div className="px-4 sm:px-6 py-3 sm:py-4">
          <div className="relative">
            <Search className="absolute left-3 sm:left-4 top-1/2 -translate-y-1/2 w-4 h-4 sm:w-5 sm:h-5 text-muted-foreground" />
            <input
              type="text"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              placeholder={t.giftPicker.searchPlaceholder}
              className="w-full h-11 sm:h-12 pl-10 sm:pl-12 pr-3 sm:pr-4 text-sm bg-muted/50 border border-border rounded-xl text-foreground placeholder:text-muted-foreground focus:outline-none focus:border-primary/50 focus:bg-muted/80 transition-all duration-200"
            />
          </div>
        </div>

        <div className="flex-1 overflow-y-auto px-4 sm:px-6 pb-4 sm:pb-6 max-h-[45vh] sm:max-h-[50vh] scroll-smooth">
          <div className="space-y-2.5 sm:space-y-3">
            <button
              onClick={() => handleToggleGift("")}
              className={`w-full flex items-center gap-3 sm:gap-4 p-3 sm:p-4 rounded-xl transition-all duration-200 active:scale-[0.98] ${
                localSelectedIds.length === 0
                  ? "bg-primary/10 border-2 border-primary/30"
                  : "hover:bg-muted/50 border-2 border-transparent"
              }`}
            >
              <div className="w-11 h-11 sm:w-12 sm:h-12 rounded-xl bg-muted flex items-center justify-center shadow-sm flex-shrink-0">
                <span className="text-xl sm:text-2xl">üéÅ</span>
              </div>
              <div className="flex-1 text-left min-w-0">
                <span className="text-foreground font-medium text-sm block truncate">{t.giftPicker.allGifts}</span>
                <p className="text-muted-foreground text-xs mt-0.5 truncate">{t.giftPicker.clearFilters}</p>
              </div>
              {localSelectedIds.length === 0 && (
                <div className="w-5 h-5 sm:w-6 sm:h-6 rounded-full bg-primary flex items-center justify-center flex-shrink-0">
                  <span className="text-white text-[10px] sm:text-xs">‚úì</span>
                </div>
              )}
            </button>

            {filteredGifts.map((gift) => (
              <button
                key={gift.id}
                onClick={() => handleToggleGift(gift.id)}
                className={`w-full flex items-center gap-3 sm:gap-4 p-3 sm:p-4 rounded-xl transition-all duration-200 active:scale-[0.98] ${
                  localSelectedIds.includes(gift.id)
                    ? "bg-primary/10 border-2 border-primary/30"
                    : "hover:bg-muted/50 border-2 border-transparent"
                }`}
              >
                <div className="w-11 h-11 sm:w-12 sm:h-12 rounded-xl overflow-hidden bg-white shadow-sm flex-shrink-0">
                  <img
                    src={gift.image}
                    alt={gift.name}
                    className="w-full h-full object-cover"
                  />
                </div>
                <div className="flex-1 text-left min-w-0">
                  <span className="text-foreground font-medium text-sm block truncate">{gift.name}</span>
                  <p className="text-muted-foreground text-xs mt-0.5 truncate">
                    {localSelectedIds.includes(gift.id) ? t.giftPicker.tapToRemove : t.giftPicker.tapToAdd}
                  </p>
                </div>
                {localSelectedIds.includes(gift.id) && (
                  <div className="w-5 h-5 sm:w-6 sm:h-6 rounded-full bg-primary flex items-center justify-center flex-shrink-0">
                    <span className="text-white text-[10px] sm:text-xs">‚úì</span>
                  </div>
                )}
              </button>
            ))}

            {filteredGifts.length === 0 && searchQuery && (
              <div className="text-center py-8">
                <div className="w-14 h-14 sm:w-16 sm:h-16 bg-muted rounded-full flex items-center justify-center mx-auto mb-3 sm:mb-4">
                  <span className="text-xl sm:text-2xl">üîç</span>
                </div>
                <p className="text-muted-foreground text-sm">{t.giftPicker.noGiftsFound}</p>
                <p className="text-muted-foreground text-xs mt-1">{t.giftPicker.tryDifferentSearch}</p>
              </div>
            )}
          </div>
        </div>

        <div className="px-4 sm:px-6 pb-4 sm:pb-6 pt-3 sm:pt-4 border-t border-border bg-background sticky bottom-0">
          <div className="flex gap-2.5 sm:gap-3">
            <Button
              variant="outline"
              onClick={handleCancel}
              className="flex-1 h-11 sm:h-12 rounded-xl border-border hover:bg-muted/50 text-sm"
            >
              {t.giftPicker.cancel}
            </Button>
            <Button
              onClick={handleApply}
              className="flex-1 h-11 sm:h-12 rounded-xl bg-primary hover:bg-primary/90 text-sm"
            >
              {t.giftPicker.apply} {localSelectedIds.length > 0 && `(${localSelectedIds.length})`}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

server/storage.ts.content:
import { type User, type InsertUser, type Gift, type Channel, type InsertChannel } from "@shared/schema";
import { AVAILABLE_GIFTS } from "@shared/gifts";
import { randomUUID } from "crypto";

export interface IStorage {
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  getAllGifts(): Promise<Gift[]>;
  getGiftById(id: string): Promise<Gift | undefined>;

  getAllChannels(): Promise<(Channel & { giftName: string; giftImage: string })[]>;
  getChannelById(id: string): Promise<(Channel & { giftName: string; giftImage: string }) | undefined>;
  createChannel(channel: InsertChannel): Promise<Channel & { giftName: string; giftImage: string }>;
  updateChannel(id: string, channel: Partial<InsertChannel>): Promise<(Channel & { giftName: string; giftImage: string }) | undefined>;
  deleteChannel(id: string): Promise<boolean>;
  searchChannelsByGiftName(query: string): Promise<(Channel & { giftName: string; giftImage: string })[]>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private gifts: Map<string, Gift>;
  private channels: Map<string, Channel>;

  constructor() {
    this.users = new Map();
    this.gifts = new Map();
    this.channels = new Map();
    this.seedGifts();
    this.seedChannels();
  }

  private seedGifts() {
    AVAILABLE_GIFTS.forEach((gift) => {
      this.gifts.set(gift.id, gift);
    });
  }

  private seedChannels() {
    const seedData: Channel[] = [
      {
        id: randomUUID(),
        channelName: "Bear Shop",
        telegramLink: "https://t.me/bears_shop",
        giftId: "box-of-chocolates",
        price: "29",
        ownerId: null,
        gifts: JSON.stringify([{ giftId: "box-of-chocolates", quantity: 1 }]),
      },
      {
        id: randomUUID(),
        channelName: "Anna's Sweets",
        telegramLink: "https://t.me/anna_sweets",
        giftId: "cherry-cake",
        price: "10.49",
        ownerId: null,
        gifts: JSON.stringify([{ giftId: "cherry-cake", quantity: 1 }]),
      },
      {
        id: randomUUID(),
        channelName: "Holiday Gifts",
        telegramLink: "https://t.me/holiday_gifts",
        giftId: "gift-bag",
        price: "15.99",
        ownerId: null,
        gifts: JSON.stringify([{ giftId: "gift-bag", quantity: 1 }]),
      },
      {
        id: randomUUID(),
        channelName: "Ice Cream Joy",
        telegramLink: "https://t.me/icecream_joy",
        giftId: "ice-cream-cone",
        price: "22.50",
        ownerId: null,
        gifts: JSON.stringify([{ giftId: "ice-cream-cone", quantity: 1 }]),
      },
    ];

    seedData.forEach((channel) => {
      this.channels.set(channel.id, channel);
    });
  }

  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.username === username,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { ...insertUser, id };
    this.users.set(id, user);
    return user;
  }

  async getAllGifts(): Promise<Gift[]> {
    return Array.from(this.gifts.values());
  }

  async getGiftById(id: string): Promise<Gift | undefined> {
    return this.gifts.get(id);
  }

  async getAllChannels(): Promise<(Channel & { giftName: string; giftImage: string })[]> {
    return Array.from(this.channels.values()).map(channel => {
      const gift = this.gifts.get(channel.giftId);
      return {
        ...channel,
        giftName: gift?.name || "",
        giftImage: gift?.image || "",
      };
    });
  }

  async getChannelById(id: string): Promise<(Channel & { giftName: string; giftImage: string }) | undefined> {
    const channel = this.channels.get(id);
    if (!channel) return undefined;

    const gift = this.gifts.get(channel.giftId);
    return {
      ...channel,
      giftName: gift?.name || "",
      giftImage: gift?.image || "",
    };
  }

  async createChannel(insertChannel: InsertChannel): Promise<Channel & { giftName: string; giftImage: string }> {
    const id = randomUUID();
    const channel: Channel = { 
      ...insertChannel,
      id,
      ownerId: insertChannel.ownerId ?? null,
      gifts: insertChannel.gifts ?? null,
    };
    this.channels.set(id, channel);

    const gift = this.gifts.get(channel.giftId);
    return {
      ...channel,
      giftName: gift?.name || "",
      giftImage: gift?.image || "",
    };
  }

  async updateChannel(id: string, updates: Partial<InsertChannel>): Promise<(Channel & { giftName: string; giftImage: string }) | undefined> {
    const channel = this.channels.get(id);
    if (!channel) return undefined;

    const updated = { ...channel, ...updates };
    this.channels.set(id, updated);

    const gift = this.gifts.get(updated.giftId);
    return {
      ...updated,
      giftName: gift?.name || "",
      giftImage: gift?.image || "",
    };
  }

  async deleteChannel(id: string): Promise<boolean> {
    return this.channels.delete(id);
  }

  async searchChannelsByGiftName(query: string): Promise<(Channel & { giftName: string; giftImage: string })[]> {
    const lowerQuery = query.toLowerCase();
    const allChannels = await this.getAllChannels();

    return allChannels.filter(channel => 
      channel.giftName.toLowerCase().includes(lowerQuery) ||
      channel.channelName.toLowerCase().includes(lowerQuery)
    );
  }
}

export const storage = new MemStorage();

