    );
  }

  if (currentPage === "profile") {
    return (
      <div className="min-h-screen bg-background text-foreground telegram-mini-app pb-20 sm:pb-24">
        <Profile />
        <BottomNav activeTab={currentPage} onTabChange={setCurrentPage} />
