import { sql } from "drizzle-orm";
import { pgTable, text, varchar, integer, timestamp, decimal } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  telegramId: text("telegram_id").unique(),
  username: text("username").notNull(),
  avatarUrl: text("avatar_url"),
  referralCode: text("referral_code").notNull().unique(),
  referredBy: varchar("referred_by").references((): any => users.id),
  language: text("language").notNull().default("en"),
  balance: decimal("balance", { precision: 10, scale: 2 }).notNull().default("0.00"),
  createdAt: timestamp("created_at").notNull().default(sql`now()`),
});

export const insertUserSchema = createInsertSchema(users).omit({
  id: true,
  referralCode: true,
  balance: true,
  createdAt: true,
});

export type InsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;

export const referrals = pgTable("referrals", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  referrerId: varchar("referrer_id").notNull().references(() => users.id),
  referredUserId: varchar("referred_user_id").notNull().references(() => users.id),
  createdAt: timestamp("created_at").notNull().default(sql`now()`),
});

export type Referral = typeof referrals.$inferSelect;

export const referralEarnings = pgTable("referral_earnings", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").notNull().references(() => users.id),
  fromUserId: varchar("from_user_id").notNull().references(() => users.id),
  amount: decimal("amount", { precision: 10, scale: 2 }).notNull(),
  depositAmount: decimal("deposit_amount", { precision: 10, scale: 2 }).notNull(),
  percentage: integer("percentage").notNull().default(3),
  createdAt: timestamp("created_at").notNull().default(sql`now()`),
});

export type ReferralEarning = typeof referralEarnings.$inferSelect;

export const gifts = pgTable("gifts", {
  id: varchar("id").primaryKey(),
  name: text("name").notNull().unique(),
  image: text("image").notNull(),
});

export type Gift = typeof gifts.$inferSelect;

export const channels = pgTable("channels", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  channelName: text("channel_name").notNull(),
  telegramLink: text("telegram_link").notNull(),
  giftId: varchar("gift_id").notNull().references(() => gifts.id),
  price: text("price").notNull(),
  ownerId: varchar("owner_id"),
  gifts: text("gifts"),
});

export const insertChannelSchema = createInsertSchema(channels).omit({
  id: true,
});

export type InsertChannel = z.infer<typeof insertChannelSchema>;
export type Channel = typeof channels.$inferSelect;

export const giftItemSchema = z.object({
  giftId: z.string(),
  quantity: z.number().int().positive(),
});

export type GiftItem = z.infer<typeof giftItemSchema>;

client/src/hooks/use-telegram-user.ts.content:
import { useEffect, useState } from 'react';

interface TelegramUser {
  id: number;
  first_name: string;
  last_name?: string;
  username?: string;
  photo_url?: string;
}

export function useTelegramUser() {
  const [user, setUser] = useState<TelegramUser | null>(null);

  useEffect(() => {
    // Check if Telegram WebApp is available
    if (typeof window !== 'undefined' && (window as any).Telegram?.WebApp) {
      const tg = (window as any).Telegram.WebApp;
      const tgUser = tg.initDataUnsafe?.user;

      if (tgUser) {
        setUser(tgUser);
      }
    }
  }, []);

  return {
    user,
    username: user?.username || user?.first_name || 'johndoe',
    avatarLetter: (user?.first_name?.[0] || user?.username?.[0] || 'J').toUpperCase(),
    photoUrl: user?.photo_url
  };
}

